import React from 'react';
import { Link } from 'react-router-dom';
import { FaHandshake, FaHome, FaShieldAlt, FaUsers } from 'react-icons/fa';
import LandingImage from '../assets/images/landing.jpg';

const features = [
  {
    icon: <FaHome />,
    title: 'Curated spaces',
    description: 'Discover furnished rooms and boutique apartments ready for move-in.'
  },
  {
    icon: <FaUsers />,
    title: 'Match by lifestyle',
    description: 'Filters help you find roommates who share your routines and interests.'
  },
  {
    icon: <FaHandshake />,
    title: 'Host-friendly tools',
    description: 'Manage listings, add galleries, and connect with tenants quickly.'
  },
  {
    icon: <FaShieldAlt />,
    title: 'Secure profiles',
    description: 'Every host and roommate profile is protected with modern security.'
  }
];

export default function Home() {
  return (
    <div className="home-page">
      <section className="home-hero gradient-card">
        <div className="home-hero__content">
          <p className="eyebrow text-uppercase fw-semibold mb-2">Smart roommate matching</p>
          <h1 className="display-5 fw-bold mb-3">Live with people you vibe with</h1>
          <p className="lead hero-subtext">
            Browse beautifully presented rooms, chat with hosts, and enroll as a host when you are ready. SmartRoommate
            brings visuals, detail, and warmth to your housing search.
          </p>
          <div className="d-flex flex-wrap gap-3 mt-4">
            <Link to="/properties" className="btn btn-primary btn-lg">
              Browse properties
            </Link>
            <Link to="/register" className="btn btn-outline-primary btn-lg">
              Become a host
            </Link>
          </div>
        </div>
        <div className="home-hero__image">
          <img src={LandingImage} alt="Roommates" className="img-fluid rounded shadow" />
        </div>
      </section>
      <section className="home-features mt-5">
        {features.map((feature) => (
          <article key={feature.title} className="feature-card shadow-sm">
            <div className="feature-icon">{feature.icon}</div>
            <h3>{feature.title}</h3>
            <p>{feature.description}</p>
          </article>
        ))}
      </section>
    </div>
  );
}
