import React, { useState, useEffect, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import API from '../services/api';

const HABIT_OPTIONS = [
  'Cinema',
  'Football',
  'Basketball',
  'Tennis',
  'Running',
  'Cycling',
  'Hiking',
  'Swimming',
  'Gym / Fitness',
  'Yoga',
  'Pilates',
  'Cooking',
  'Baking',
  'Coffee shops',
  'Tea lover',
  'Wine tasting',
  'Craft beer',
  'Board games',
  'Card games',
  'Video games',
  'Esports',
  'Anime',
  'Manga',
  'Reading',
  'Writing',
  'Photography',
  'Videography',
  'Art galleries',
  'Museums',
  'Concerts',
  'Live music',
  'Festivals',
  'Travel',
  'Road trips',
  'Camping',
  'Beach days',
  'Shopping',
  'Thrifting',
  'Fashion',
  'Interior design',
  'DIY projects',
  'Gardening',
  'Plants',
  'Pets / Dogs',
  'Pets / Cats',
  'Volunteering',
  'Meditation',
  'Language learning',
  'Tech & gadgets',
  'Podcasts',
  'Stand-up comedy',
  'Dancing'
];

const MIN_HABITS = 3;
const MAX_HABITS = 8;

const parseHabits = (value) =>
  (value || '')
    .split(',')
    .map((item) => item.trim())
    .filter(Boolean);

const formatHabits = (list) => list.join(', ');

const calculateAge = (birthdate) => {
  if (!birthdate) return null;
  const dob = new Date(birthdate);
  if (Number.isNaN(dob.getTime())) return null;
  const diff = Date.now() - dob.getTime();
  const ageDate = new Date(diff);
  return Math.abs(ageDate.getUTCFullYear() - 1970);
};

export default function Profile() {
  const [profile, setProfile] = useState(null);
  const [form, setForm] = useState({ birthdate: '', gender: '', location: '', habits: [] });
  const [msg, setMsg] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const load = async () => {
      try {
        const res = await API.get('/users/me');
        const habitsList = parseHabits(res.data.habits);
        setProfile(res.data);
        setForm({
          birthdate: res.data.birthdate ? res.data.birthdate.slice(0, 10) : '',
          gender: res.data.gender || '',
          location: res.data.location || '',
          habits: habitsList
        });
      } catch (err) {
        if (err.response?.status === 401) {
          setError('Please log in to view your profile.');
        } else {
          setError('Unable to load your profile right now.');
        }
      } finally {
        setLoading(false);
      }
    };
    load();
  }, []);

  const handleHabitToggle = (habit) => {
    setForm((prev) => {
      const alreadySelected = prev.habits.includes(habit);
      if (alreadySelected) {
        return { ...prev, habits: prev.habits.filter((h) => h !== habit) };
      }
      if (prev.habits.length >= MAX_HABITS) return prev;
      return { ...prev, habits: [...prev.habits, habit] };
    });
  };

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMsg('');
    setError('');

    if (form.habits.length < MIN_HABITS || form.habits.length > MAX_HABITS) {
      setError(`Choose at least ${MIN_HABITS} and at most ${MAX_HABITS} interests.`);
      return;
    }

    if (!form.birthdate) {
      setError('Please add your birthdate so we can show your age.');
      return;
    }

    const age = calculateAge(form.birthdate);
    const payload = {
      birthdate: form.birthdate,
      age,
      gender: form.gender || null,
      location: form.location || '',
      budget: null,
      habits: formatHabits(form.habits)
    };

    try {
      setSaving(true);
      await API.put('/users/me', payload);
      setMsg('Profile updated');
      setProfile((prev) => ({ ...prev, ...payload }));
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.message || 'Unable to update profile right now.');
    } finally {
      setSaving(false);
    }
  };

  const ageDisplay = useMemo(() => calculateAge(form.birthdate || profile?.birthdate), [form.birthdate, profile]);

  if (loading) return <div>Loading...</div>;

  if (error && !profile) {
    return (
      <div className="alert alert-warning d-flex justify-content-between align-items-center">
        <span>{error}</span>
        <button type="button" className="btn btn-primary btn-sm" onClick={() => navigate('/login')}>
          Go to login
        </button>
      </div>
    );
  }

  if (!profile) return null;

  return (
    <div className="row g-4">
      <div className="col-lg-7">
        <div className="card border-0 shadow-sm">
          <div className="card-body">
            <p className="eyebrow text-uppercase mb-1">Your profile</p>
            <h2 className="h4 mb-3">Personal details</h2>
            <p className="mb-1"><strong>Name:</strong> {profile.name}</p>
            <p className="mb-1"><strong>Email:</strong> {profile.email}</p>
            {ageDisplay !== null && <p className="mb-1"><strong>Age:</strong> {ageDisplay} years</p>}
            {profile.location && <p className="mb-1"><strong>Location:</strong> {profile.location}</p>}
            {form.habits.length > 0 && (
              <div className="mt-3">
                <p className="mb-1"><strong>Interests:</strong></p>
                <div className="pill-grid">
                  {form.habits.map((habit) => (
                    <span key={habit} className="pill pill-selected">{habit}</span>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
      <div className="col-lg-5">
        <div className="card border-0 shadow-sm">
          <div className="card-body">
            <p className="eyebrow text-uppercase mb-1">Improve your matches</p>
            <h3 className="h5">Update profile</h3>
            {msg && <div className="alert alert-info mt-2">{msg}</div>}
            {error && profile && <div className="alert alert-warning mt-2">{error}</div>}
            <form className="mt-3" onSubmit={handleSubmit}>
              <div className="mb-3">
                <label className="form-label">Birthdate *</label>
                <input
                  type="date"
                  name="birthdate"
                  value={form.birthdate}
                  onChange={handleChange}
                  className="form-control"
                  required
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Gender</label>
                <select name="gender" value={form.gender} onChange={handleChange} className="form-select">
                  <option value="">Select</option>
                  <option value="male">Male</option>
                  <option value="female">Female</option>
                  <option value="other">Prefer not to say</option>
                </select>
              </div>
              <div className="mb-3">
                <label className="form-label">Location</label>
                <input name="location" value={form.location} onChange={handleChange} className="form-control" />
              </div>
              <div className="mb-3">
                <div className="d-flex justify-content-between align-items-center mb-1">
                  <label className="form-label mb-0">Interests (select {MIN_HABITS}-{MAX_HABITS}) *</label>
                  <small className="text-muted">{form.habits.length} selected</small>
                </div>
                <div className="pill-grid selectable">
                  {HABIT_OPTIONS.map((option) => {
                    const selected = form.habits.includes(option);
                    const disabled = !selected && form.habits.length >= MAX_HABITS;
                    return (
                      <button
                        type="button"
                        key={option}
                        className={`pill ${selected ? 'pill-selected' : ''}`}
                        onClick={() => handleHabitToggle(option)}
                        disabled={disabled}
                      >
                        {option}
                      </button>
                    );
                  })}
                </div>
                <small className="text-muted">
                  Weâ€™ll use these to improve AI roommate compatibility. Pick what genuinely matters to you.
                </small>
              </div>
              <button className="btn btn-primary" type="submit" disabled={saving}>
                {saving ? 'Saving...' : 'Save profile'}
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
}
