import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import API from '../services/api';
import { useRole } from '../context/RoleContext';

const emptyFormState = {
  title: '',
  location: '',
  price: '',
  description: '',
  rooms: '',
  property_type: ''
};

export default function Properties() {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [formError, setFormError] = useState('');
  const [form, setForm] = useState(emptyFormState);
  const [editingId, setEditingId] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const navigate = useNavigate();
  const isAuthenticated = Boolean(localStorage.getItem('token'));
  const { isHost, isTransitioning, pendingMode } = useRole();
  const canManageListings = isAuthenticated && isHost;

  useEffect(() => {
    const fetchProperties = async () => {
      try {
        setLoading(true);
        const res = await API.get('/properties');
        setProperties(res.data);
        setError('');
      } catch (err) {
        console.error(err);
        setError('Unable to load properties right now.');
      } finally {
        setLoading(false);
      }
    };

    fetchProperties();
  }, []);

  useEffect(() => {
    const loadUser = async () => {
      if (!localStorage.getItem('token')) {
        setCurrentUser(null);
        return;
      }

      try {
        const res = await API.get('/users/me');
        setCurrentUser(res.data);
      } catch (err) {
        if (err.response?.status === 401) {
          setCurrentUser(null);
        } else {
          console.error(err);
        }
      }
    };

    loadUser();
  }, []);

  const resetForm = () => {
    setForm(emptyFormState);
    setEditingId(null);
    setFormError('');
  };

  const openForm = (property = null) => {
    if (!canManageListings) return;
    if (property) {
      setForm({
        title: property.title,
        location: property.location,
        price: property.price,
        description: property.description || '',
        rooms: property.rooms ?? '',
        property_type: property.property_type || ''
      });
      setEditingId(property.id);
    } else {
      resetForm();
    }
    setShowForm(true);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setFormError('');
    if (!localStorage.getItem('token')) {
      navigate('/login');
      return;
    }

    if (!form.title.trim() || !form.location.trim()) {
      setFormError('Title and location are required.');
      return;
    }

    const priceValue = parseFloat(form.price);
    if (Number.isNaN(priceValue)) {
      setFormError('Please enter a valid monthly price.');
      return;
    }

    const roomsValue = form.rooms === '' ? null : parseInt(form.rooms, 10);
    if (roomsValue !== null && Number.isNaN(roomsValue)) {
      setFormError('Rooms must be a number.');
      return;
    }

    const payload = {
      title: form.title.trim(),
      location: form.location.trim(),
      price: priceValue,
      description: form.description.trim(),
      rooms: roomsValue,
      property_type: form.property_type
    };

    try {
      setSubmitting(true);
      if (editingId) {
        await API.put(`/properties/${editingId}`, payload);
      } else {
        await API.post('/properties', payload);
      }
      const res = await API.get('/properties');
      setProperties(res.data);
      setError('');
      setShowForm(false);
      resetForm();
    } catch (err) {
      console.error(err);
      setFormError(err.response?.data?.message || 'Unable to save property.');
    } finally {
      setSubmitting(false);
    }
  };

  const handleDelete = async (propertyId) => {
    if (!localStorage.getItem('token')) {
      navigate('/login');
      return;
    }
    if (!window.confirm('Delete this property?')) return;

    try {
      await API.delete(`/properties/${propertyId}`);
      setProperties((prev) => prev.filter((p) => p.id !== propertyId));
      setError('');
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.message || 'Unable to delete property.');
    }
  };

  const ownedByCurrentUser = (property) => currentUser && property.user_id === currentUser.id;

  useEffect(() => {
    if (!canManageListings && showForm) {
      setShowForm(false);
      setForm(emptyFormState);
      setEditingId(null);
      setFormError('');
    }
  }, [canManageListings, showForm]);

  return (
    <div>
      <div className="d-flex flex-wrap justify-content-between align-items-center mb-4 gap-3">
        <div>
          <h1 className="h3 mb-1">Available Properties</h1>
          <p className="text-muted mb-0">
            Browse the list of apartments and rooms our community has shared. Create and manage your own listings once
            you&apos;re signed in.
          </p>
        </div>
        {canManageListings && (
          <button type="button" className="btn btn-primary" onClick={() => openForm()}>
            Add Property
          </button>
        )}
      </div>
      {isAuthenticated && isTransitioning && pendingMode === 'host' && (
        <div className="alert alert-secondary">Loading host tools for you. This will only take a moment.</div>
      )}
      {isAuthenticated && !isHost && !isTransitioning && (
        <div className="alert alert-info">Switch to host mode to add new listings or edit your properties.</div>
      )}
      {error && <div className="alert alert-danger">{error}</div>}
      {loading ? (
        <div>Loading properties...</div>
      ) : properties.length ? (
        <div className="row g-3">
          {properties.map((property) => (
            <div className="col-md-6 col-lg-4" key={property.id}>
              <div className="card h-100 shadow-sm">
                <div className="card-body d-flex flex-column">
                  <div className="d-flex justify-content-between align-items-start">
                    <h5>{property.title}</h5>
                    <span className="badge text-bg-light">${Number(property.price).toFixed(2)}</span>
                  </div>
                  <p className="mb-1 text-muted">{property.location}</p>
                  {property.description && <p className="flex-grow-1">{property.description}</p>}
                  <small className="text-muted">
                    Listed by {property.owner_name} {property.rooms ? `- ${property.rooms} rooms` : ''}{' '}
                    {property.property_type ? `- ${property.property_type}` : ''}
                  </small>
                  {ownedByCurrentUser(property) && canManageListings && (
                    <div className="mt-3 d-flex gap-2">
                      <button
                        type="button"
                        className="btn btn-outline-secondary btn-sm"
                        onClick={() => openForm(property)}
                      >
                        Edit
                      </button>
                      <button
                        type="button"
                        className="btn btn-outline-danger btn-sm"
                        onClick={() => handleDelete(property.id)}
                      >
                        Delete
                      </button>
                    </div>
                  )}
                </div>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div className="alert alert-info">No properties have been listed yet.</div>
      )}
      {showForm && (
        <div className="card mt-4">
          <div className="card-body">
            <div className="d-flex justify-content-between align-items-center mb-3">
              <h5 className="mb-0">{editingId ? 'Edit property' : 'Add a property'}</h5>
              <button type="button" className="btn-close" aria-label="Close" onClick={() => setShowForm(false)}></button>
            </div>
            {formError && <div className="alert alert-danger">{formError}</div>}
            {!isAuthenticated && (
              <div className="alert alert-warning">
                You need an account before you can create listings.{' '}
                <button type="button" className="btn btn-link p-0" onClick={() => navigate('/login')}>
                  Log in
                </button>{' '}
                or{' '}
                <button type="button" className="btn btn-link p-0" onClick={() => navigate('/register')}>
                  register
                </button>
                .
              </div>
            )}
            <form onSubmit={handleSubmit}>
              <div className="row g-3">
                <div className="col-md-6">
                  <label className="form-label">Title *</label>
                  <input name="title" className="form-control" value={form.title} onChange={handleChange} required />
                </div>
                <div className="col-md-6">
                  <label className="form-label">Location *</label>
                  <input name="location" className="form-control" value={form.location} onChange={handleChange} required />
                </div>
                <div className="col-md-4">
                  <label className="form-label">Monthly price *</label>
                  <input
                    name="price"
                    className="form-control"
                    value={form.price}
                    onChange={handleChange}
                    required
                  />
                </div>
                <div className="col-md-4">
                  <label className="form-label">Rooms</label>
                  <input name="rooms" className="form-control" value={form.rooms} onChange={handleChange} />
                </div>
                <div className="col-md-4">
                  <label className="form-label">Type</label>
                  <select
                    name="property_type"
                    className="form-select"
                    value={form.property_type}
                    onChange={handleChange}
                  >
                    <option value="">Select type</option>
                    <option value="room">Room</option>
                    <option value="flat">Flat</option>
                    <option value="apartment">Apartment</option>
                  </select>
                </div>
                <div className="col-12">
                  <label className="form-label">Description</label>
                  <textarea
                    name="description"
                    className="form-control"
                    rows="3"
                    value={form.description}
                    onChange={handleChange}
                  ></textarea>
                </div>
              </div>
              <div className="mt-3 d-flex gap-2">
                <button className="btn btn-primary" type="submit" disabled={submitting}>
                  {submitting ? 'Saving...' : editingId ? 'Save Changes' : 'Create Property'}
                </button>
                <button
                  type="button"
                  className="btn btn-outline-secondary"
                  onClick={() => {
                    setShowForm(false);
                    resetForm();
                  }}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}
