import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { clearAuthToken } from '../services/api';
import { useRole } from '../context/RoleContext';

export default function Navbar() {
  const navigate = useNavigate();
  const token = localStorage.getItem('token');
  const { isHost, toggleMode, switchToTenant } = useRole();

  const handleLogout = () => {
    localStorage.removeItem('token');
    clearAuthToken();
    switchToTenant();
    navigate('/login');
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-light bg-light">
      <div className="container-fluid">
        <Link className="navbar-brand" to="/">SmartRoommate</Link>
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav ms-auto">
            <li className="nav-item"><Link className="nav-link" to="/">Home</Link></li>
            <li className="nav-item"><Link className="nav-link" to="/properties">Properties</Link></li>
            {token ? (
              <>
                <li className="nav-item d-flex align-items-center">
                  <span className="text-muted small me-2">{isHost ? 'Host mode' : 'Tenant mode'}</span>
                  <button type="button" className="btn btn-outline-primary btn-sm" onClick={toggleMode}>
                    {isHost ? 'Switch to tenant mode' : 'Switch to host mode'}
                  </button>
                </li>
                <li className="nav-item"><Link className="nav-link" to="/profile">Profile</Link></li>
                <li className="nav-item">
                  <button type="button" className="btn btn-link nav-link" onClick={handleLogout}>Logout</button>
                </li>
              </>
            ) : (
              <>
                <li className="nav-item"><Link className="nav-link" to="/login">Login</Link></li>
                <li className="nav-item"><Link className="nav-link" to="/register">Register</Link></li>
              </>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
}
