import ApartmentInterior from '../assets/images/Apartment_interior_pexels-houzlook-3797991.jpg';
import BedroomInterior from '../assets/images/Bedroom_interior_13395.-Download-Free-3D-Bedroom-Interior-Model-by-Nguyen-The-Dinh-1-scaled.jpg';
import BosmalApartment from '../assets/images/Bosmal_Apartment_City_Center_Sarajevo.jpg';
import CitySuite from '../assets/images/467427096.jpg';
import InteriorVilla from '../assets/images/Interior_villa_pexels-fotoaibe-1571460.jpg';
import ModernVilla from '../assets/images/Villa_1777_9215_1720971222.jpg';
import DefaultImage from '../assets/images/No_Image_Available.jpg';

export const PROPERTY_IMAGE_POOL = [
  ApartmentInterior,
  BedroomInterior,
  InteriorVilla,
  ModernVilla,
  BosmalApartment,
  CitySuite
];

export const DEFAULT_PROPERTY_IMAGE = DefaultImage;

export function getAccentImage(seed = 0) {
  if (!PROPERTY_IMAGE_POOL.length) return DEFAULT_PROPERTY_IMAGE;
  const normalizedSeed = Math.abs(Number(seed) || 0);
  return PROPERTY_IMAGE_POOL[normalizedSeed % PROPERTY_IMAGE_POOL.length];
}

export function buildFallbackGallery(desiredSize = 4, seed = 0) {
  if (!PROPERTY_IMAGE_POOL.length) return [DEFAULT_PROPERTY_IMAGE];
  const gallery = [];
  for (let i = 0; i < desiredSize; i += 1) {
    gallery.push(PROPERTY_IMAGE_POOL[(i + Math.abs(Number(seed) || 0)) % PROPERTY_IMAGE_POOL.length]);
  }
  return gallery;
}
