import React from 'react';
import { Link } from 'react-router-dom';
import LandingImage from '../assets/images/landing.jpg';

export default function Home() {
  return (
    <div className="row align-items-center g-4">
      <div className="col-md-6">
        <h1 className="display-5 fw-bold">Smart Roommate & Apartment Matching</h1>
        <p className="lead">
          Quickly list your place, explore available rooms, and keep your roommate profile up to date. This MVP
          focuses on the essential flow so you can get a feel for the experience end-to-end.
        </p>
        <ul>
          <li></li>
        </ul>
        <Link to="/properties" className="btn btn-primary btn-lg mt-2">
          Browse Properties
        </Link>
      </div>
      <div className="col-md-6 text-center">
        <img src={LandingImage} alt="Roommates" className="img-fluid rounded shadow-sm" />
      </div>
    </div>
  );
}
