import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import API from '../services/api';

export default function Profile() {
  const [profile, setProfile] = useState(null);
  const [form, setForm] = useState({ age: '', gender: '', location: '', budget: '', habits: '' });
  const [msg, setMsg] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    const load = async () => {
      try {
        const res = await API.get('/users/me');
        setProfile(res.data);
        setForm({
          age: res.data.age || '',
          gender: res.data.gender || '',
          location: res.data.location || '',
          budget: res.data.budget || '',
          habits: res.data.habits || ''
        });
      } catch (err) {
        if (err.response?.status === 401) {
          setError('Please log in to view your profile.');
        } else {
          setError('Unable to load your profile right now.');
        }
      } finally {
        setLoading(false);
      }
    };
    load();
  }, []);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await API.put('/users/me', form);
      setMsg('Profile updated');
    } catch (err) {
      console.error(err);
      setMsg('Unable to update profile right now.');
    }
  };

  if (loading) return <div>Loading...</div>;

  if (error) {
    return (
      <div className="alert alert-warning d-flex justify-content-between align-items-center">
        <span>{error}</span>
        <button type="button" className="btn btn-primary btn-sm" onClick={() => navigate('/login')}>
          Go to login
        </button>
      </div>
    );
  }

  if (!profile) return null;

  return (
    <div className="row">
      <div className="col-md-8">
        <h2>Profile</h2>
        <p><strong>Name:</strong> {profile.name}</p>
        <p><strong>Email:</strong> {profile.email}</p>
        {msg && <div className="alert alert-info">{msg}</div>}
        <form onSubmit={handleSubmit}>
          <div className="mb-3">
            <label className="form-label">Age</label>
            <input name="age" value={form.age} onChange={handleChange} className="form-control" />
          </div>
          <div className="mb-3">
            <label className="form-label">Gender</label>
            <input name="gender" value={form.gender} onChange={handleChange} className="form-control" />
          </div>
          <div className="mb-3">
            <label className="form-label">Location</label>
            <input name="location" value={form.location} onChange={handleChange} className="form-control" />
          </div>
          <div className="mb-3">
            <label className="form-label">Budget</label>
            <input name="budget" value={form.budget} onChange={handleChange} className="form-control" />
          </div>
          <div className="mb-3">
            <label className="form-label">Habits / Notes</label>
            <textarea name="habits" value={form.habits} onChange={handleChange} className="form-control"></textarea>
          </div>
          <button className="btn btn-primary" type="submit">Save</button>
        </form>
      </div>
    </div>
  );
}
