import React, { useEffect, useMemo, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaBed, FaDollarSign, FaImages, FaMapMarkerAlt, FaPen, FaTrash } from 'react-icons/fa';
import API from '../services/api';
import { useRole } from '../context/RoleContext';
import { buildFallbackGallery, DEFAULT_PROPERTY_IMAGE, getAccentImage } from '../constants/propertyMedia';

const galleryDelimiter = /[\n,]+/;

const emptyFormState = {
  title: '',
  location: '',
  price: '',
  description: '',
  rooms: '',
  property_type: '',
  mainImage: '',
  galleryImages: ''
};

const priceFormatter = new Intl.NumberFormat('en-US', {
  style: 'currency',
  currency: 'USD',
  maximumFractionDigits: 0
});

const formatGalleryText = (images = []) => (images?.length ? images.join('\n') : '');

const parseGalleryField = (value) => {
  if (!value) return [];
  if (Array.isArray(value)) return value;
  return value
    .split(galleryDelimiter)
    .map((item) => item.trim())
    .filter(Boolean);
};

const resolveHeroImage = (property, fallbackSeed) => {
  const candidate = property?.main_image_url?.trim() || property?.gallery_images?.[0];
  if (candidate) return candidate;
  return getAccentImage(fallbackSeed) || DEFAULT_PROPERTY_IMAGE;
};

const resolveGallery = (property, fallbackSeed) => {
  if (property?.gallery_images?.length) return property.gallery_images;
  if (property?.main_image_url) return [property.main_image_url];
  return buildFallbackGallery(4, fallbackSeed);
};

const formatPrice = (value) => priceFormatter.format(Number(value) || 0);

export default function Properties() {
  const [properties, setProperties] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [formError, setFormError] = useState('');
  const [form, setForm] = useState(emptyFormState);
  const [editingId, setEditingId] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const navigate = useNavigate();
  const isAuthenticated = Boolean(localStorage.getItem('token'));
  const { isHost, isTransitioning, pendingMode } = useRole();
  const canManageListings = isAuthenticated && isHost;

  useEffect(() => {
    const fetchProperties = async () => {
      try {
        setLoading(true);
        const res = await API.get('/properties');
        setProperties(res.data);
        setError('');
      } catch (err) {
        console.error(err);
        setError('Unable to load properties right now.');
      } finally {
        setLoading(false);
      }
    };

    fetchProperties();
  }, []);

  useEffect(() => {
    const loadUser = async () => {
      if (!localStorage.getItem('token')) {
        setCurrentUser(null);
        return;
      }

      try {
        const res = await API.get('/users/me');
        setCurrentUser(res.data);
      } catch (err) {
        if (err.response?.status === 401) {
          setCurrentUser(null);
        } else {
          console.error(err);
        }
      }
    };

    loadUser();
  }, []);

  const resetForm = () => {
    setForm(emptyFormState);
    setEditingId(null);
    setFormError('');
  };

  const openForm = (property = null) => {
    if (!canManageListings) return;
    if (property) {
      setForm({
        title: property.title,
        location: property.location,
        price: property.price,
        description: property.description || '',
        rooms: property.rooms ?? '',
        property_type: property.property_type || '',
        mainImage: property.main_image_url || '',
        galleryImages: formatGalleryText(property.gallery_images)
      });
      setEditingId(property.id);
    } else {
      resetForm();
    }
    setShowForm(true);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setFormError('');
    if (!localStorage.getItem('token')) {
      navigate('/login');
      return;
    }

    if (!form.title.trim() || !form.location.trim()) {
      setFormError('Title and location are required.');
      return;
    }

    const priceValue = parseFloat(form.price);
    if (Number.isNaN(priceValue)) {
      setFormError('Please enter a valid monthly price.');
      return;
    }

    const roomsValue = form.rooms === '' ? null : parseInt(form.rooms, 10);
    if (roomsValue !== null && Number.isNaN(roomsValue)) {
      setFormError('Rooms must be a number.');
      return;
    }

    const galleryList = parseGalleryField(form.galleryImages);

    const payload = {
      title: form.title.trim(),
      location: form.location.trim(),
      price: priceValue,
      description: form.description.trim(),
      rooms: roomsValue,
      property_type: form.property_type,
      mainImage: form.mainImage.trim(),
      galleryImages: galleryList
    };

    try {
      setSubmitting(true);
      if (editingId) {
        await API.put(`/properties/${editingId}`, payload);
      } else {
        await API.post('/properties', payload);
      }
      const res = await API.get('/properties');
      setProperties(res.data);
      setError('');
      setShowForm(false);
      resetForm();
    } catch (err) {
      console.error(err);
      setFormError(err.response?.data?.message || 'Unable to save property.');
    } finally {
      setSubmitting(false);
    }
  };

  const handleDelete = async (propertyId) => {
    if (!localStorage.getItem('token')) {
      navigate('/login');
      return;
    }
    if (!window.confirm('Delete this property?')) return;

    try {
      await API.delete(`/properties/${propertyId}`);
      setProperties((prev) => prev.filter((p) => p.id !== propertyId));
      setError('');
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.message || 'Unable to delete property.');
    }
  };

  const ownedByCurrentUser = (property) => currentUser && property.user_id === currentUser.id;

  useEffect(() => {
    if (!canManageListings && showForm) {
      setShowForm(false);
      setForm(emptyFormState);
      setEditingId(null);
      setFormError('');
    }
  }, [canManageListings, showForm]);

  const highlightCopy = useMemo(
    () =>
      properties.length
        ? `We found ${properties.length} ${properties.length === 1 ? 'place' : 'places'} that match a mix of cozy rooms and vibrant apartments.`
        : 'No spaces have been listed yet — be the very first to add yours!',
    [properties.length]
  );

  const handleCardClick = (property) => {
    navigate(`/properties/${property.id}`, { state: { property } });
  };

  return (
    <div className="properties-page">
      <section className="properties-hero gradient-card">
        <div>
          <p className="eyebrow text-uppercase fw-semibold mb-2">Explore</p>
          <h1 className="properties-title mb-3">Beautiful shared homes curated for modern roommates</h1>
          <p className="mb-4 text-light-emphasis">{highlightCopy}</p>
          {canManageListings && (
            <button type="button" className="btn btn-light btn-lg shadow-sm" onClick={() => openForm()}>
              List a new space
            </button>
          )}
        </div>
        <div className="properties-hero-meta">
          <div>
            <span className="hero-number">{properties.length || '00'}</span>
            <p className="mb-0 text-light">active listings</p>
          </div>
          <div>
            <span className="hero-number">
              {properties.filter((p) => p.property_type?.toLowerCase() === 'apartment').length || '0'}
            </span>
            <p className="mb-0 text-light">city apartments</p>
          </div>
        </div>
      </section>

      {isAuthenticated && isTransitioning && pendingMode === 'host' && (
        <div className="alert alert-secondary shadow-sm mt-4">Loading host tools for you. This will only take a moment.</div>
      )}
      {isAuthenticated && !isHost && !isTransitioning && (
        <div className="alert alert-info shadow-sm mt-4">Switch to host mode to add new listings or edit your properties.</div>
      )}
      {error && <div className="alert alert-danger shadow-sm mt-4">{error}</div>}

      <section className="properties-grid-wrapper mt-4">
        {loading ? (
          <div className="card border-0 shadow-sm p-4 text-center">Loading properties...</div>
        ) : properties.length ? (
          <div className="property-grid">
            {properties.map((property, index) => {
              const heroSeed = property.id ?? index;
              const heroImage = resolveHeroImage(property, heroSeed) || DEFAULT_PROPERTY_IMAGE;
              const galleryCount = resolveGallery(property, heroSeed).length;

              return (
                <article
                  key={property.id}
                  className="property-card shadow-sm"
                  role="button"
                  tabIndex={0}
                  onClick={() => handleCardClick(property)}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter') handleCardClick(property);
                  }}
                >
                  <div className="property-card__media">
                    <img src={heroImage} alt={property.title} loading="lazy" />
                    <span className="property-card__type">{property.property_type || 'Listing'}</span>
                    <div className="property-card__overlay">
                      <span>View details</span>
                    </div>
                  </div>
                  <div className="property-card__body">
                    <div className="d-flex justify-content-between align-items-start gap-2 mb-2">
                      <div>
                        <h2 className="property-card__title">{property.title}</h2>
                        <p className="property-card__location">
                          <FaMapMarkerAlt className="me-1 text-primary" />
                          {property.location}
                        </p>
                      </div>
                      <div className="property-card__price">
                        <FaDollarSign className="me-1" />
                        {formatPrice(property.price)}
                        <span className="text-muted">/mo</span>
                      </div>
                    </div>
                    {property.description && <p className="property-card__description">{property.description}</p>}
                    <div className="property-card__meta">
                      <span>
                        <FaBed className="me-1" />
                        {property.rooms ? `${property.rooms} rooms` : 'Flexible rooms'}
                      </span>
                      <span>
                        <FaImages className="me-1" />
                        {galleryCount} photos
                      </span>
                    </div>
                    <div className="property-card__footer">
                      <span>Hosted by {property.owner_name}</span>
                      {ownedByCurrentUser(property) && canManageListings && (
                        <div className="property-card__actions">
                          <button
                            type="button"
                            className="btn btn-outline-primary btn-sm"
                            onClick={(event) => {
                              event.stopPropagation();
                              openForm(property);
                            }}
                          >
                            <FaPen className="me-1" />
                            Edit
                          </button>
                          <button
                            type="button"
                            className="btn btn-outline-danger btn-sm"
                            onClick={(event) => {
                              event.stopPropagation();
                              handleDelete(property.id);
                            }}
                          >
                            <FaTrash className="me-1" />
                            Delete
                          </button>
                        </div>
                      )}
                    </div>
                  </div>
                </article>
              );
            })}
          </div>
        ) : (
          <div className="card border-0 shadow-sm p-4 text-center text-muted">
            There are no properties yet. Hosts can be the first to share a beautiful space.
          </div>
        )}
      </section>

      {showForm && (
        <div className="property-form card shadow border-0 mt-4">
          <div className="card-body">
            <div className="d-flex flex-wrap justify-content-between align-items-center mb-3 gap-2">
              <div>
                <p className="eyebrow mb-1 text-muted">{editingId ? 'Update listing' : 'Add listing'}</p>
                <h2 className="h4 mb-0">{editingId ? 'Edit property' : 'Add a property'}</h2>
              </div>
              <button type="button" className="btn-close" aria-label="Close" onClick={() => setShowForm(false)}></button>
            </div>
            {formError && <div className="alert alert-danger">{formError}</div>}
            {!isAuthenticated && (
              <div className="alert alert-warning">
                You need an account before you can create listings.{' '}
                <button type="button" className="btn btn-link p-0" onClick={() => navigate('/login')}>
                  Log in
                </button>{' '}
                or{' '}
                <button type="button" className="btn btn-link p-0" onClick={() => navigate('/register')}>
                  register
                </button>
                .
              </div>
            )}
            <form onSubmit={handleSubmit}>
              <div className="row g-3">
                <div className="col-md-6">
                  <label className="form-label">Title *</label>
                  <input name="title" className="form-control" value={form.title} onChange={handleChange} required />
                </div>
                <div className="col-md-6">
                  <label className="form-label">Location *</label>
                  <input name="location" className="form-control" value={form.location} onChange={handleChange} required />
                </div>
                <div className="col-md-4">
                  <label className="form-label">Monthly price *</label>
                  <input name="price" className="form-control" value={form.price} onChange={handleChange} required />
                </div>
                <div className="col-md-4">
                  <label className="form-label">Rooms</label>
                  <input name="rooms" className="form-control" value={form.rooms} onChange={handleChange} />
                </div>
                <div className="col-md-4">
                  <label className="form-label">Type</label>
                  <select name="property_type" className="form-select" value={form.property_type} onChange={handleChange}>
                    <option value="">Select type</option>
                    <option value="room">Room</option>
                    <option value="flat">Flat</option>
                    <option value="apartment">Apartment</option>
                  </select>
                </div>
                <div className="col-md-6">
                  <label className="form-label">Main image URL</label>
                  <input
                    name="mainImage"
                    className="form-control"
                    value={form.mainImage}
                    onChange={handleChange}
                    placeholder="https://example.com/cover.jpg"
                  />
                </div>
                <div className="col-md-6">
                  <label className="form-label d-flex justify-content-between align-items-center">
                    <span>Gallery image URLs</span>
                    <small className="text-muted">Comma or line separated</small>
                  </label>
                  <textarea
                    name="galleryImages"
                    className="form-control"
                    rows="2"
                    value={form.galleryImages}
                    onChange={handleChange}
                    placeholder="https://example.com/photo1.jpg, https://example.com/photo2.jpg"
                  ></textarea>
                </div>
                <div className="col-12">
                  <label className="form-label">Description</label>
                  <textarea
                    name="description"
                    className="form-control"
                    rows="3"
                    value={form.description}
                    onChange={handleChange}
                  ></textarea>
                </div>
              </div>
              <div className="mt-3 d-flex gap-2">
                <button className="btn btn-primary" type="submit" disabled={submitting}>
                  {submitting ? 'Saving...' : editingId ? 'Save changes' : 'Create property'}
                </button>
                <button
                  type="button"
                  className="btn btn-outline-secondary"
                  onClick={() => {
                    setShowForm(false);
                    resetForm();
                  }}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}
