import React, { useState, useEffect, useMemo } from 'react';
import { useNavigate } from 'react-router-dom';
import API from '../services/api';

const calculateAge = (birthdate) => {
  if (!birthdate) return null;
  const dob = new Date(birthdate);
  if (Number.isNaN(dob.getTime())) return null;
  const diff = Date.now() - dob.getTime();
  const ageDate = new Date(diff);
  return Math.abs(ageDate.getUTCFullYear() - 1970);
};

export default function Profile() {
  const [profile, setProfile] = useState(null);
  const [form, setForm] = useState({ birthdate: '', gender: '', location: '', bio: '' });
  const [msg, setMsg] = useState('');
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const load = async () => {
      try {
        const res = await API.get('/users/me');
        setProfile(res.data);
        setForm({
          birthdate: res.data.birthdate ? res.data.birthdate.slice(0, 10) : '',
          gender: res.data.gender || '',
          location: res.data.location || '',
          bio: res.data.habits || ''
        });
      } catch (err) {
        if (err.response?.status === 401) {
          setError('Please log in to view your profile.');
        } else {
          setError('Unable to load your profile right now.');
        }
      } finally {
        setLoading(false);
      }
    };
    load();
  }, []);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMsg('');
    setError('');

    if (!form.birthdate) {
      setError('Please add your birthdate so we can show your age.');
      return;
    }

    if (!form.gender) {
      setError('Please select your gender.');
      return;
    }

    if (!form.bio.trim()) {
      setError('Add a short bio about yourself (max 30 characters).');
      return;
    }

    if (form.bio.trim().length > 30) {
      setError('Bio must be 30 characters or fewer.');
      return;
    }

    const age = calculateAge(form.birthdate);
    const payload = {
      birthdate: form.birthdate,
      age,
      gender: form.gender || null,
      location: form.location || '',
      budget: null,
      habits: form.bio.trim()
    };

    try {
      setSaving(true);
      await API.put('/users/me', payload);
      setMsg('Profile updated');
      setProfile((prev) => ({ ...prev, ...payload }));
    } catch (err) {
      console.error(err);
      setError(err.response?.data?.message || 'Unable to update profile right now.');
    } finally {
      setSaving(false);
    }
  };

  const ageDisplay = useMemo(() => calculateAge(form.birthdate || profile?.birthdate), [form.birthdate, profile]);

  if (loading) return <div>Loading...</div>;

  if (error && !profile) {
    return (
      <div className="alert alert-warning d-flex justify-content-between align-items-center">
        <span>{error}</span>
        <button type="button" className="btn btn-primary btn-sm" onClick={() => navigate('/login')}>
          Go to login
        </button>
      </div>
    );
  }

  if (!profile) return null;

  return (
    <div className="profile-page">
      <div className="card border-0 shadow-sm mb-4">
        <div className="card-body">
          <p className="eyebrow text-uppercase mb-1">Your profile</p>
          <h2 className="h4 mb-3">Personal details</h2>
          <p className="mb-1"><strong>Name:</strong> {profile.name}</p>
          <p className="mb-1"><strong>Email:</strong> {profile.email}</p>
          {ageDisplay !== null && <p className="mb-1"><strong>Age:</strong> {ageDisplay} years</p>}
          {profile.location && <p className="mb-1"><strong>Location:</strong> {profile.location}</p>}
          {form.bio && (
            <p className="mb-0 mt-2">
              <strong>My bio:</strong> {form.bio}
            </p>
          )}
        </div>
      </div>

      <div className="card border-0 shadow-sm">
        <div className="card-body">
          <p className="eyebrow text-uppercase mb-1">Improve your matches</p>
          <h3 className="h5">Update profile</h3>
          {msg && <div className="alert alert-info mt-2">{msg}</div>}
          {error && profile && <div className="alert alert-warning mt-2">{error}</div>}
          <form className="mt-3" onSubmit={handleSubmit}>
            <div className="mb-3">
              <label className="form-label">Birthdate *</label>
              <input
                type="date"
                name="birthdate"
                value={form.birthdate}
                onChange={handleChange}
                className="form-control"
                required
              />
            </div>
            <div className="mb-3">
              <label className="form-label d-block">Gender *</label>
              <div className="d-flex gap-3">
                <div className="form-check">
                  <input
                    className="form-check-input"
                    type="radio"
                    name="gender"
                    id="gender-male"
                    value="male"
                    checked={form.gender === 'male'}
                    onChange={handleChange}
                    required
                  />
                  <label className="form-check-label" htmlFor="gender-male">
                    Male
                  </label>
                </div>
                <div className="form-check">
                  <input
                    className="form-check-input"
                    type="radio"
                    name="gender"
                    id="gender-female"
                    value="female"
                    checked={form.gender === 'female'}
                    onChange={handleChange}
                    required
                  />
                  <label className="form-check-label" htmlFor="gender-female">
                    Female
                  </label>
                </div>
              </div>
            </div>
            <div className="mb-3">
              <label className="form-label">Location</label>
              <input name="location" value={form.location} onChange={handleChange} className="form-control" />
            </div>
            <div className="mb-3">
              <div className="d-flex justify-content-between align-items-center mb-1">
                <label className="form-label mb-0">My bio *</label>
                <small className="text-muted">{form.bio.length}/30</small>
              </div>
              <input
                name="bio"
                value={form.bio}
                onChange={(e) => {
                  if (e.target.value.length <= 30) handleChange(e);
                }}
                className="form-control"
                placeholder="Short sentence about you"
                required
              />
            </div>
            <button className="btn btn-primary" type="submit" disabled={saving}>
              {saving ? 'Saving...' : 'Save profile'}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}
